import React from 'react';

const PratoCard = ({ prato }) => {
  const precoFormatado = Number(prato.preco).toFixed(2);

  return (
    <div 
      style={{
        border: '1px solid #ccc',
        padding: '15px',
        margin: '10px',
        width: '220px',
        borderRadius: '8px',
        boxShadow: '0 0 8px rgba(0,0,0,0.1)',
      }}
    >
      <img 
        src={prato.urlImagem || 'https://via.placeholder.com/200x120?text=Sem+Imagem'} 
        alt={prato.nome} 
        style={{
          width: '100%', 
          height: '120px', 
          objectFit: 'cover', 
          borderRadius: '6px'
        }} 
      />
      <h3 style={{ margin: '10px 0 5px 0' }}>{prato.nome}</h3>
      <p style={{ margin: '0 0 5px 0' }}>{prato.descricao}</p>
      <p><b>Pre√ßo:</b> R$ {precoFormatado}</p>
      <p><b>Categoria:</b> {prato.categoria}</p>
      <p><b>Status:</b> {prato.disponibilidade}</p>
    </div>
  );
};

export default PratoCard;
