import React from 'react';

const PratoCard = ({ prato }) => {
  return (
    <div style={{
      borderRadius: '10px',
      boxShadow: '0 4px 8px rgba(0,0,0,0.1)',
      overflow: 'hidden',
      width: '250px',
      backgroundColor: 'white',
      transition: 'transform 0.2s',
      cursor: 'pointer'
    }}
    onMouseEnter={e => e.currentTarget.style.transform = 'scale(1.05)'}
    onMouseLeave={e => e.currentTarget.style.transform = 'scale(1)'}
    >
      <img 
        src={prato.urlImagem || 'https://via.placeholder.com/250x150?text=Imagem+Indisponível'} 
        alt={prato.nome} 
        style={{width: '100%', height: '150px', objectFit: 'cover'}} 
      />
      <div style={{padding: '15px'}}>
        <h3 style={{marginBottom: '8px', color: '#0d6efd'}}>{prato.nome}</h3>
        <p style={{fontSize: '0.9rem', color: '#555', minHeight: '50px'}}>{prato.descricao}</p>
        <p style={{fontWeight: 'bold', marginTop: '10px'}}>R$ {prato.preco.toFixed(2)}</p>
        <p style={{fontSize: '0.8rem', color: prato.disponibilidade === 'DISPONIVEL' ? 'green' : 'red'}}>
          {prato.disponibilidade === 'DISPONIVEL' ? 'Disponível' : 'Indisponível'}
        </p>
      </div>
    </div>
  );
};

export default PratoCard;
