import React, { useEffect, useState } from 'react';
import PratoCard from '../components/PratoCard';
import { buscarPratos } from '../services/pratosService';

const ListarPratos = () => {
  const [pratos, setPratos] = useState([]);

  useEffect(() => {
    buscarPratos()
      .then(setPratos)
      .catch(() => alert('Erro ao carregar pratos.'));
  }, []);

  if (pratos.length === 0) {
    return <p style={{textAlign: 'center', marginTop: '40px'}}>Nenhum prato encontrado.</p>;
  }

  return (
    <div style={{ display: 'flex', flexWrap: 'wrap', justifyContent: 'center', gap: '20px', padding: '20px' }}>
      {pratos.map(prato => (
        <PratoCard key={prato.id} prato={prato} />
      ))}
    </div>
  );
};

export default ListarPratos;
