import { useState } from 'react';
import { cadastrarPrato } from '../services/pratosService';

function Cadastro() {
  const [nome, setNome] = useState('');
  const [descricao, setDescricao] = useState('');
  const [preco, setPreco] = useState('');
  const [categoria, setCategoria] = useState('ENTRADA');
  const [disponibilidade, setDisponibilidade] = useState('EM_ESTOQUE');
  const [urlImagem, setUrlImagem] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();
    const prato = { nome, descricao, preco, categoria, disponibilidade, urlImagem };
    try {
      await cadastrarPrato(prato);
      alert('Prato cadastrado com sucesso!');
    } catch (error) {
      console.error('Erro ao cadastrar prato:', error);
      alert('Erro no cadastro!');
    }
  };

  return (
    <div>
      <h2>Cadastrar Prato</h2>
      <form onSubmit={handleSubmit}>
        <input placeholder="Nome" value={nome} onChange={(e) => setNome(e.target.value)} />
        <input placeholder="Descrição" value={descricao} onChange={(e) => setDescricao(e.target.value)} />
        <input placeholder="Preço" value={preco} onChange={(e) => setPreco(e.target.value)} />
        
        <select value={categoria} onChange={(e) => setCategoria(e.target.value)}>
          <option value="ENTRADA">Entrada</option>
          <option value="PRATOPRINCIPAL">Principal</option>
          <option value="SOBREMESA">Sobremesa</option>
          <option value="BEBIDA">Bebida</option>
        </select>

        <select value={disponibilidade} onChange={(e) => setDisponibilidade(e.target.value)}>
          <option value="EM_ESTOQUE">Em estoque</option>
          <option value="ESGOTADO">Indisponível</option>
        </select>

        <input placeholder="URL da Imagem" value={urlImagem} onChange={(e) => setUrlImagem(e.target.value)} />
        
        <button type="submit">Cadastrar</button>
      </form>
    </div>
  );
}

export default Cadastro;
