import { useState } from 'react';
import { cadastrarPrato } from '../services/pratosService';
import { useNavigate } from 'react-router-dom';

function Cadastro() {
  const navigate = useNavigate();
  const [form, setForm] = useState({
    nome: '',
    descricao: '',
    preco: '',
    categoria: '',
    disponibilidade: '',
    urlImagem: ''
  });

  const [loading, setLoading] = useState(false);

  function handleChange(e) {
    const { name, value } = e.target;
    setForm({ ...form, [name]: value });
  }

  async function handleSubmit(e) {
    e.preventDefault();
    setLoading(true);
    try {
      await cadastrarPrato({
        ...form,
        preco: parseFloat(form.preco) // garante que o preço seja enviado como número
      });
      alert('Prato cadastrado com sucesso!');
      navigate('/listar');
    } catch (error) {
      console.error('Erro no cadastro:', error);
      alert('Erro ao cadastrar o prato. Verifique os dados ou tente novamente.');
    } finally {
      setLoading(false);
    }
  }

  return (
    <div style={{ padding: '20px' }}>
      <h2>Cadastro de Prato</h2>
      <form onSubmit={handleSubmit}>
        <input
          name="nome"
          placeholder="Nome"
          value={form.nome}
          onChange={handleChange}
          required
        /><br />

        <input
          name="descricao"
          placeholder="Descrição"
          value={form.descricao}
          onChange={handleChange}
          required
        /><br />

        <input
          name="preco"
          placeholder="Preço"
          type="number"
          step="0.01"
          value={form.preco}
          onChange={handleChange}
          required
        /><br />

        <input
          name="categoria"
          placeholder="Categoria"
          value={form.categoria}
          onChange={handleChange}
          required
        /><br />

        <input
          name="disponibilidade"
          placeholder="Disponibilidade"
          value={form.disponibilidade}
          onChange={handleChange}
          required
        /><br />

        <input
          name="urlImagem"
          placeholder="URL da imagem"
          value={form.urlImagem}
          onChange={handleChange}
        /><br />

        <button type="submit" disabled={loading}>
          {loading ? 'Cadastrando...' : 'Cadastrar'}
        </button>
      </form>
    </div>
  );
}

export default Cadastro;
